mapper.select:
SELECT INQUIRY_NUM, INQUIRY_CATEGORY_NUM, TITLE, MEM_NUM, INPUT_DATE, ANSWER_FLAG, ANSWER_MANAGER_ID, ANSWER_CONTENTS, ANSWER_DATE, DELETE_FLAG, DELETE_DATE, RNUM FROM ( SELECT INQUIRY_NUM, INQUIRY_CATEGORY_NUM, TITLE, MEM_NUM, INPUT_DATE, ANSWER_FLAG, ANSWER_MANAGER_ID, ANSWER_CONTENTS, ANSWER_DATE, DELETE_FLAG, DELETE_DATE, ROW_NUMBER() OVER(ORDER BY INPUT_DATE DESC) RNUM FROM INQUIRY
DELETE_FLAG = 'N'
AND (
INSTR(TITLE, #{keyword}) > 0
INSTR(CONTENTS, #{keyword}) > 0
)
) WHERE RNUM BETWEEN #{startNum} AND #{endNum}

mapper.update:
UPDATE INQUIRY
SET delete_flag = 'Y'
WHERE INQUIRY_NUM = #{inquiry_num}
