mapper.select:
SELECT REP_NUM, MEM_NUM_REP, MANAGER_ID, GOODS_NUM, CATEGORY, TITLE, CONTENTS, INPUT_DATE, PROCESS_STATUS_CODE, PROCESS_CONTENTS, PROCESS_DATE, RNUM FROM ( SELECT REP_NUM, MEM_NUM_REP, MANAGER_ID, GOODS_NUM, CATEGORY, TITLE, CONTENTS, INPUT_DATE, PROCESS_STATUS_CODE, PROCESS_CONTENTS, PROCESS_DATE, ROW_NUMBER() OVER(ORDER BY INPUT_DATE DESC) RNUM FROM REPORT
AND (
INSTR(TITLE, #{keyword}) > 0
INSTR(CONTENTS, #{keyword}) > 0
)
) WHERE RNUM BETWEEN #{startNum} AND #{endNum}
